using System;
using System.Collections.Generic;
using System.ComponentModel;
using System.Data;
using System.Drawing;
using System.Linq;
using System.Text;
using System.Windows.Forms;
using System.Data.Entity;

namespace Coffee
{
    public partial class Stores : Form
    {
        public Stores()
        {
            InitializeComponent();
            // This line of code is generated by Data Source Configuration Wizard
            // Instantiate a new DBContext
            Coffee.AutoweighEntities dbContext = new AutoweighEntities(coffee.ConnectionString());
            // Call the Load method to get the data for the given DbSet from the database.
            dbContext.Items.Load();
            // This line of code is generated by Data Source Configuration Wizard
            itemsBindingSource.DataSource = dbContext.Items.Local.ToBindingList();

        }

        private void itemsBindingSource_CurrentChanged(object sender, EventArgs e)
        {
            if (itemsBindingSource.Current != null)
            {
                Coffee.AutoweighEntities db = new AutoweighEntities(coffee.ConnectionString());
                variantsBindingSource.DataSource = db.Item_Variants.Where(o => o.No == ((Item)itemsBindingSource.Current).No).ToList();
            }
        }

        private void QtyoutLinkEdit1_Click(object sender, EventArgs e)
        {
            if (variantsBindingSource.Current != null)
            {
                Posted_Debts s = new Posted_Debts((Item_Variant)variantsBindingSource.Current);
                s.ShowDialog();

            }
        }


        private void QtyinLinkEdit1_Click(object sender, EventArgs e)
        {
            if (variantsBindingSource.Current != null)
            {
                Stocks s = new Stocks((Item_Variant)variantsBindingSource.Current);
                s.ShowDialog();

            }
        }

