namespace Weigh
{
    public partial class Users : Form
    {
        public Users()
        {
            InitializeComponent();
            // This line of code is generated by Data Source Configuration Wizard
            // Instantiate a new DBContext

            // Call the Load method to get the data for the given DbSet from the database.
            AutoweighEntities.Db.Users.Load();
            // This line of code is generated by Data Source Configuration Wizard
            usersBindingSource.DataSource = AutoweighEntities.Db.Users.Local.ToBindingList();
          
        }

        private void saveToolStripButton_Click(object sender, EventArgs e)
        {
            dataGridView1.EndEdit();
            AutoweighEntities.Db.SaveChanges(true);
        }

        private void repositoryItemTextEdit1_Leave(object sender, EventArgs e)
        {
            // gridView1.SetRowCellValue(gridView1.FocusedRowHandle, gridView1.FocusedColumn, AutoweighEntities.GetMd5Hash(gridView1.GetFocusedDisplayText()));
        }

        private void dataGridView1_CellFormatting(object sender, DataGridViewCellFormattingEventArgs e)
        {
            if (dataGridView1.Columns[e.ColumnIndex].Name == "passwordDataGridViewTextBoxColumn" && e.Value != null)
            {
                dataGridView1.Rows[e.RowIndex].Tag = e.Value;
                e.Value = new String('*', e.Value.ToString().Length);
            }
        }

        private void dataGridView1_EditingControlShowing(object sender, DataGridViewEditingControlShowingEventArgs e)
        {
            TextBox passwordText = e.Control as TextBox;
            if (passwordText != null)
            {
                passwordText.UseSystemPasswordChar = true;
            }
            if (dataGridView1.CurrentRow.Tag != null)
                e.Control.Text = dataGridView1.CurrentRow.Tag.ToString();
        }

    }
}


